mapper.update:
update FAQ
set faq_question=#{faq_question}, faq_answer= #{faq_answer},faq_category=#{faq_category}
where faq_key=#{faq_key}

mapper.select:
select *
from FAQ
where faq_writer like #{word}
where faq_question like #{word}
where faq_answer like #{word}
where faq_category like #{word}
order by faq_key desc
limit #{start}, #{size}

select count(*)
from FAQ
where faq_writer like #{word}
where faq_question like #{word}
where faq_answer like #{word}
where faq_category like #{word}

mapper.update:
update FAQ
set faq_vcnt=faq_vcnt+1
where faq_key=#{faq_key}
