mapper.update:
update notice
set notice_title= #{notice_title}, notice_contents=#{notice_contents}, notice_type= #{notice_type}
where notice_key=#{notice_key}

mapper.select:
select *
from notice
where notice_writer like #{word}
where notice_title like #{word}
where notice_contents like #{word}
order by notice_key desc
limit #{start}, #{size}

select count(*)
from notice
where notice_writer like #{word}
where notice_title like #{word}
where notice_contents like #{word}

mapper.update:
update notice
set notice_vcnt=notice_vcnt+1
where notice_key=#{notice_key}
