mapper.update:
update notice
set notice_title= #{notice_title}, notice_contents=#{notice_contents}, notice_type= #{notice_type}
where notice_key=#{notice_key}

mapper.select:
select * from notice
notice_type = 1 notice_type = 2 notice_type = ''
order by notice_key desc limit #{start}, #{size}

mapper.update:
update notice
set notice_vcnt=notice_vcnt+1
where notice_key=#{notice_key}
