mapper.update:
update QA
set qa_title= #{qa_title}, qa_contents=#{qa_contents}
where qa_key=#{qa_key}

mapper.select:
 		select * 
		from QA
		where qa_writer like #{word}
		where qa_title like #{word}
		where qa_contents like #{word}
		order by qa_key desc
		limit #{start}, #{size} ;

 		select count(*)
 		from QA
 		where qa_writer like #{word}
		where qa_title like #{word}
		where qa_contents like #{word}

mapper.update:
update QA
set qa_vcnt=qa_vcnt+1
where qa_key=#{qa_key}
